function Buy(t,n,r,i){$.ajax({type:"POST",url:n,data:{productId:t},dataType:"text",success:function(t){ChangeTotalPriceInformation(r,i,t)},error:function(t,n,i){$(r).text("error during buying!")}})}function ChangeTotalPriceInformation(t,n,r){$(t).text(parseFloat(r).toLocaleString(n,{style:"currency",currency:"CZK",minimumFractionDigits:2,maximumFractionDigits:2}))}